import React from 'react';
import { Text, View, Image, StyleSheet, TouchableOpacity, } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import cover from '../assets/images/cover/001.jpg';
import useTheme from '../hooks/useTheme';

const { radius, font_size, font_color } = useTheme();

const styles = StyleSheet.create({
  publisherName: {
    fontSize: font_size.size.sm,
  },
  publishDate: {
    fontSize: font_size.size.xs,
    color: font_color.text.secondary,
  },
  ImageWrapper: {
    overflow: 'hidden',
    marginVertical: 12,
    borderRadius: radius.normal,
    flexWrap: 'wrap',
    width: '50%',
  },
  coverText: {
    fontWeight: 'bold',
    marginBottom: 8,
    fontSize: font_size.size.sm,
  },
  metaWrapper: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  metaItem: {
    flexDirection: 'row',
    alignItems: 'center',
  },
});

const MangaItem = ({ navigation }) => (
  <View style={{ marginBottom: 24, flexDirection: 'row', justifyContent: 'space-between' }}>

    <TouchableOpacity
      style={styles.ImageWrapper}
      onPress={() => navigation.navigate('Details')}
    >
      <Image
        style={{
          width: 140,
          height: 200,
          resizeMode: 'cover',
        }}
        source={cover}
      />
      <Text style={styles.coverText}>World Without Blessings</Text>
    </TouchableOpacity>

    <TouchableOpacity
      style={styles.ImageWrapper}
      onPress={() => navigation.navigate('Details')}
    >
      <Image
        style={{
          width: 140,
          height: 200,
          resizeMode: 'cover',
        }}
        source={cover}
      />
    </TouchableOpacity>

  </View>
);

export default MangaItem;
