import React from 'react';
import { ImageBackground, StyleSheet, View, Text, Image, TouchableOpacity, } from 'react-native';
import Constants from 'expo-constants';
import LoginButton from '../components/LoginButton';
import useTheme from '../hooks/useTheme';
import logo from '../assets/images/logo/mascot_logo.png';
import bg1 from '../assets/images/background/bg1.jpg';

const { font_color, font_size } = useTheme();

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 24,
    backgroundColor: 'white',
    paddingTop: Constants.statusBarHeight,
  },
  centered: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 212,
    height: 196,
  },
  brand: {
    fontSize: font_size.size.xl,
    fontWeight: 'bold',
    marginBottom: 4,
  },
  motto: {
    fontSize: font_size.size.sm,
    color: font_color.text.secondary,
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center",
    padding: 0,
  },
  row: { flexDirection: 'row' },
});

const WelcomeScreen = ({ navigation }) => (
  <View style={styles.container}>
    <ImageBackground source={bg1} style={styles.backgroundImage}>
      <View style={[styles.centered, { marginTop: 55 }]}>
        <Image
          style={styles.logo}
          source={logo}
        />
      </View>
      <View style={[styles.centered, { marginTop: 22 }]}>
        <Text style={styles.brand}>
          Cookapp
        </Text>
        <Text style={styles.motto}>
          Sharing recipes of hapiness world wide
        </Text>
      </View>
      <View style={{ marginVertical: 24 }}>
        <LoginButton
          icon={{ name: 'email', color: font_color.text.primary }}
          label={{ placeholder: 'Sign in with Email', color: font_color.text.primary }}
          style={{ backgroundColor: font_color.primary.main, marginBottom: 8 }}
          onPress={() => navigation.navigate('Signin')}
        />
      </View>
      <View style={[styles.centered, styles.row]}>
        <Text style={{ color: font_color.text.primary }}>
          Don&#8216;t have an account?
        </Text>
        <TouchableOpacity
          style={{ marginLeft: 4 }}
          onPress={() => navigation.navigate('Signup')}
        >
          <Text style={{ color: font_color.primary.main }}>Sign up</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  </View>
);

export default WelcomeScreen;
