import React from 'react';
import { Text, View, Image, StyleSheet, TouchableOpacity, } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import cover from '../assets/images/cover/001.jpg';
import useTheme from '../hooks/useTheme';

const { radius, font_size, font_color } = useTheme();

const styles = StyleSheet.create({
  publisherContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
  publisherName: {
    fontSize: font_size.size.sm,
  },
  publishDate: {
    fontSize: font_size.size.xs,
    color: font_color.text.secondary,
  },
  recipeImageWrapper: {
    overflow: 'hidden',
    marginVertical: 12,
    borderRadius: radius.normal,
    flexDirection: 'row',
  },
  recipeText: {
    fontWeight: 'bold',
    marginBottom: 8,
    fontSize: font_size.size.sm,
  },
  metaWrapper: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  metaItem: {
    flexDirection: 'row',
    alignItems: 'center',
  },
});

const MangaItem = ({ navigation }) => (
  <View style={{ marginBottom: 24 }}>

    <TouchableOpacity
      style={styles.recipeImageWrapper}
      onPress={() => navigation.navigate('Details')}
    >
      <Image
        style={{
          width: 120,
          height: 180,
          resizeMode: 'cover',
        }}
        source={cover}
      />
    </TouchableOpacity>

    <TouchableOpacity
      style={styles.recipeImageWrapper}
      onPress={() => navigation.navigate('Details')}
    >
      <Image
        style={{
          width: 120,
          height: 180,
          resizeMode: 'cover',
        }}
        source={cover}
      />
    </TouchableOpacity>

    <View>
      <Text style={styles.recipeText}>World Without Blessings</Text>
      <View style={styles.metaWrapper}>
        <View style={styles.metaItem}>
          <MaterialCommunityIcons
            name="clock"
            size={16}
            style={{ color: font_color.text.secondary, marginRight: 4 }}
          />
          <Text style={{ color: font_color.text.secondary }}>8m</Text>
        </View>
        <View style={styles.metaWrapper}>
          <TouchableOpacity style={[styles.metaItem, { marginRight: 8 }]}>
            <MaterialCommunityIcons
              name="thumb-up"
              size={16}
              style={{ color: font_color.text.secondary, marginRight: 4 }}
            />
            <Text style={{ color: font_color.text.secondary }}>564k</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  </View>
);

export default MangaItem;
