import React from 'react';
import { ImageBackground, StyleSheet, View, Text, Image, TouchableOpacity, TextInput } from 'react-native';
import Constants from 'expo-constants';
import useTheme from '../hooks/useTheme';
import logo from '../assets/images/logo/mascot_logo.png';
import bg1 from '../assets/images/background/bg1.png';


const { font_color, font_size, radius } = useTheme();

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: font_color.common.white,
  },
  centered: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 312,
    height: 234,
  },
  head: {
    marginTop: 32,
    marginBottom: 16,
    fontWeight: 'bold',
    fontSize: font_size.size.xl,
  },
  input: {
    borderColor: font_color.common.gray.main,
    borderWidth: 1,
    paddingHorizontal: 16,
    paddingVertical: 8,
    fontFamily: 'Roboto',
    borderRadius: radius.normal,
  },
  button: {
    backgroundColor: font_color.primary.main,
    borderRadius: radius.pills,
    padding: 16,
    alignItems: 'center',
  },
  alert: {
    padding: 16,
    marginBottom: 16,
    borderRadius: radius.normal,
    backgroundColor: font_color.error.main,
  },
});

const RegisterScreen = ({ navigation }) => {
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [shown, setShown] = React.useState(false);
  const [alert, setAlert] = React.useState(false);

  const handlerLogin = () => {
    if (email === '' && password === '') {
      setAlert(true);
      return;
    }
    navigation.navigate('Main');
  };

  return (
    <ScrollView style={styles.container}>
      <View style={{
        padding: 24,
        paddingTop: Constants.statusBarHeight,
      }}
      >
        <View style={[styles.centered]}>
          <Image
            style={styles.illustration}
            source={logo}
          />
        </View>
        <Text style={styles.head}>Welcome back</Text>
        {alert ? (
          <View style={styles.alert}>
            <Text style={{ color: font_color.common.white }}>
              Email and password should not empty
            </Text>
          </View>
        ) : null}
        <View>
          <TextInput
            value={email}
            onChangeText={(text) => setEmail(text)}
            type="text"
            placeholder="Email"
            keyboardType="email-address"
            style={[styles.input, { marginBottom: 8 }]}
          />
          <TextInput
            secureTextEntry={!shown}
            value={password}
            onChangeText={(text) => setPassword(text)}
            textContentType="password"
            placeholder="Password"
            style={styles.input}
          />
          <TouchableOpacity
            style={{ marginTop: 8, marginBottom: 24 }}
            onPress={() => setShown(!shown)}
          >
            <Text style={{ color: font_color.text.secondary }}>
              {shown ? 'Hide' : 'Show'}
              {' '}
              password
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.button}
            onPress={handlerLogin}
          >
            <Text style={{ color: font_color.common.white }}>Login</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};

export default RegisterScreen;
